import React, { useRef, useState } from 'react'
import Header from './Header'
import {  checkValidData } from '../utils/validate';

const Login = () => {

const [isSigninform,setSigninForm] = useState(true);
const [msg,setmsg]= useState(null);
const email = useRef(null)
const password  =useRef(null);

  const toggleSignInForm = () =>{
    setSigninForm(!isSigninform);

  }

  const handleButtonClick = () =>{
    //first of all validate  email and password

    

    
   const msg = (checkValidData(email.current.value,password.current.value))
   setmsg(msg)
   
  }
  return (
   
    <div >
      <Header/>
      <div className='absolute'>
      <img src='https://assets.nflxext.com/ffe/siteui/vlv3/00103100-5b45-4d4f-af32-342649f1bda5/64774cd8-5c3a-4823-a0bb-1610d6971bd4/IN-en-20230821-popsignuptwoweeks-perspective_alpha_website_medium.jpg'/>
      </div>
      <form  onSubmit={(e)=>e.preventDefault()} className='absolute w-3/12 bg-black p-12 my-36 mx-auto left-0 right-0 text-white rounded-lg opacity-80'>
      <h1 className='font-bold text-3xl py-4' >{isSigninform?"Sign In":"Sign Up"}</h1>
      {
        !isSigninform&& <input type='text' placeholder='Full Name' className='my-4 p-2 w-full  bg-gray-800'/>
      }
      <input ref={email} type='text' placeholder='Email' className='my-4 p-2 w-full bg-gray-800'/>
      <input ref={password} type='text' placeholder='Password' className='my-4 p-2 w-full bg-gray-800'/>
      <p className='red'>{msg}</p>
      <button   className='p-4 my-6 bg-red-500 w-full rounded-lg' onClick={handleButtonClick} >Submit</button>
      <p className='py-4 cursor-pointer'  onClick={toggleSignInForm}>{isSigninform?"New to Netflix?Sign Up Now":"Already registered?Sign in"}</p>
    </form>
    </div>
  )
}

export default Login
